document.addEventListener("DOMContentLoaded", function () {
  var header = document.querySelector('header');
  var main = document.querySelector('main');
  var introSelector = header.getAttribute('data-intro-section');
  window.addEventListener('scroll', function () {
    if (window.scrollY > 75) {
      header.classList.add('fixed-top');
      // add padding top to show content behind navbar
      document.body.style.paddingTop = header.querySelector('.navbar').offsetHeight + 'px';
      if (introSelector) {
        // move intro to main.
        var intro = document.querySelector(introSelector);
        if (intro) {
          for (var i = 0; i < header.classList.length; i++) {
            if (header.classList[i] != 'fixed-top') {
              intro.classList.add(header.classList[i]);
            }
          }
          var fragment = document.createDocumentFragment();
          fragment.appendChild(intro);
          main.prepend(fragment);
        }
      }
    } else {
      header.classList.remove('fixed-top');
      // remove padding top from body
      document.body.style.paddingTop = '0';
      if (introSelector) {
        // restore intro position.
        var _intro = document.querySelector(introSelector);
        if (_intro) {
          for (var _i = 0; _i < header.classList.length; _i++) {
            _intro.classList.remove(header.classList[_i]);
          }
          var fragment = document.createDocumentFragment();
          fragment.appendChild(_intro);
          header.append(fragment);
        }
      }
    }
  });
});